'use strict';

var util = require('util');

var Transform = require('readable-stream/transform');

function Swallow() {
  Transform.call(this, {objectMode: true});
}
util.inherits(Swallow, Transform);

Swallow.prototype._transform = function _transform(chunk, enc, cb) {
  cb();
};

module.exports = function swallow() {
  return new Swallow();
};
module.exports.Swallow = Swallow;
